Create an AuthManager class to handle authentication state:
```kotlin
class AuthManager @Inject constructor(
    private val userRepository: UserRepository,
    private val dataStore: DataStore<Preferences>
) {
    private val userIdKey = intPreferencesKey("user_id")
    private val usernameKey = stringPreferencesKey("username")

    private val _currentUser = MutableStateFlow<User?>(null)
    val currentUser: StateFlow<User?> = _currentUser

    val isAuthenticated: Flow<Boolean> = currentUser.map { it != null }

    init {
        viewModelScope.launch {
            dataStore.data.first().let { preferences ->
                val userId = preferences[userIdKey]
                val username = preferences[usernameKey]

                if (userId != null && username != null) {
                    _currentUser.value = User(userId, username)
                }
            }
        }
    }

    suspend fun saveAuthenticatedUser(user: User) {
        dataStore.edit { preferences ->
            preferences[userIdKey] = user.id
            preferences[usernameKey] = user.username
        }
        _currentUser.value = user
    }

    suspend fun logout() {
        dataStore.edit { preferences ->
            preferences.remove(userIdKey)
            preferences.remove(usernameKey)
        }
        _currentUser.value = null
    }
}
