class HomeViewModel(private val expenseRepository: ExpenseRepository) : ViewModel() {
    private val _userId = MutableStateFlow<Int?>(null)
    private val _currentMonth = MutableStateFlow(YearMonth.now())

    private val _expenses = MutableStateFlow<List<Expense>>(emptyList())
    val expenses: StateFlow<List<Expense>> = _expenses

    private val _recurringExpenses = MutableStateFlow<List<Expense>>(emptyList())
    val recurringExpenses: StateFlow<List<Expense>> = _recurringExpenses

    val currentMonth: StateFlow<YearMonth> = _currentMonth

    fun setUserId(userId: Int) {
        _userId.value = userId
        loadExpenses()
    }

    fun changeMonth(offset: Int) {
        _currentMonth.value = _currentMonth.value.plusMonths(offset.toLong())
        loadExpenses()
    }

    private fun loadExpenses() {
        val userId = _userId.value ?: return

        viewModelScope.launch {
            expenseRepository.getExpensesByMonth(userId, _currentMonth.value)
                .collect { expenses ->
                    _expenses.value = expenses
                }
        }

        viewModelScope.launch {
            expenseRepository.getRecurringExpenses(userId)
                .collect { expenses ->
                    _recurringExpenses.value = expenses
                }
        }
    }

    fun getExpensesGroupedByWeek(): Map<Int, List<Expense>> {
        return expenses.value.groupBy { expense ->
            expense.date.get(WeekFields.of(Locale.getDefault()).weekOfMonth())
        }
    }

    fun getTotalExpenses(): Double {
        return expenses.value.sumOf { it.amount }
    }
}
