class NewExpenseViewModel(private val expenseRepository: ExpenseRepository) : ViewModel() {
    private val _expenseState = MutableStateFlow<ExpenseState>(ExpenseState.Initial)
    val expenseState: StateFlow<ExpenseState> = _expenseState

    private val _userId = MutableStateFlow<Int?>(null)

    fun setUserId(userId: Int) {
        _userId.value = userId
    }

    fun saveExpense(
        date: LocalDate,
        merchant: String,
        amount: Double,
        isRecurring: Boolean,
        notes: String?
    ) {
        val userId = _userId.value ?: return

        viewModelScope.launch {
            _expenseState.value = ExpenseState.Saving

            if (merchant.isBlank()) {
                _expenseState.value = ExpenseState.Error("Merchant name cannot be empty")
                return@launch
            }

            if (amount <= 0) {
                _expenseState.value = ExpenseState.Error("Amount must be greater than zero")
                return@launch
            }

            val expense = Expense(
                id = 0,
                userId = userId,
                date = date,
                merchant = merchant,
                amount = amount,
                isRecurring = isRecurring,
                notes = notes?.takeIf { it.isNotBlank() }
            )

            val result = expenseRepository.addExpense(expense)
            result.fold(
                onSuccess = { id ->
                    _expenseState.value = ExpenseState.Success(id)
                },
                onFailure = { error ->
                    _expenseState.value = ExpenseState.Error(error.message ?: "Failed to save expense")
                }
            )
        }
    }

    fun resetState() {
        _expenseState.value = ExpenseState.Initial
    }

    sealed class ExpenseState {
        object Initial : ExpenseState()
        object Saving : ExpenseState()
        data class Success(val id: Long) : ExpenseState()
        data class Error(val message: String) : ExpenseState()
    }
}
